import Navigation from "./navigation/navigation";
import Products from "./products/products";
import Recommended from "./recommended/recommended";
import Sidebar from "./sidebar/sidebar";
import data from "./db/data"
import { useState } from "react";


function App(){
    const [dataa,newdata] = useState(data);
    const [data2,newdata2] = useState(data);
    const [data3,newdata3] = useState(data);
    const [data1,newdata1] = useState(data);

    function totalchange()
    {
        newdata(data1.filter(element =>
            data2.some(item => item.title === element.title) &&
            data3.some(item => item.title === element.title)
          ));
    }

function change1(title)
{
    var filter=data.filter(item=>item.title.toLowerCase().startsWith(title.toLowerCase()));
    newdata1(filter);
    totalchange();
}

function change2(name)
{
    var filter=[];
    if(name==="All Products")
    filter=data;
    else
    filter=data.filter(item=>name===item.company);
    newdata2(filter);
    totalchange();
}

function change3(name1,name2)
{
    const check = ["$0 - 50","$50 - 100","$100 - 150","Over $150"];
    var filter=[];
    if(name2==="All")
    filter=data;
    else
    filter=data.filter(item=>name2.toLowerCase()===item[name1.toLowerCase()].toLowerCase());
    newdata3(filter);
    totalchange(data1,data2,filter);
}



return(<div>
<Sidebar onSidepress={change3}/>
<Navigation onAdd={change1} />
<Recommended onPress={change2}/>

<Products data={dataa}/>

</div>

    );
}






export default App ;
